package com.example.material_didactico.repository;

import java.util.List;

import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;


import com.example.material_didactico.entities.SA_LibroMast;
import com.example.material_didactico.models.LibrosDisponibles;

public interface SA_LibroMastRepository extends JpaRepository<SA_LibroMast, Long>{
	
	@Query(value = "SELECT \r\n"
			+ "				a.IdLibro AS idlibro, \r\n"
			+ "				a.DescripcionLocal AS descripcionlocal,\r\n"
			+ "				a.DescripcionExtranjera AS descripcionextranjera,\r\n"
			+ "				a.DescripcionCompleta AS descripcioncompleta,\r\n"
			+ "				a.Estado AS estado,\r\n"
			+ "				a.TipoManejo AS tipomanejo,\r\n"
			+ "				a.CodigoVirtualFlag AS codigovirtualflag,\r\n"
			+ "				'80.00' AS precio ,\r\n"
			+ "				'data:image/jpeg;base64,' AS imagen\r\n"
			+ "				FROM SA_LibroMast a\r\n"
			+ "				WHERE a.DisponiblePortalFlag = 'S' AND a.Estado = 'A'",
		       nativeQuery = true)
	
		List<LibrosDisponibles> findAllAvailableBooks();


}
